mapper.select:
SELECT fopen.lottery_fill_open_id, fopen.open_number, fopen.lottery_fill_creater_date, fopen.fill_l_bi, fopen.award_l_bi, fopen.award_user_id, fopen.status, type.lottery_remark, type.lottery_clazz_id, type.lottery_name, type.lottery_rule, type.lottery_type, Sum(fuser.lottery_fill_buy_qty) AS buyQty FROM lottery_fill_open AS fopen INNER JOIN lottery_type AS type ON fopen.lottery_type_id = type.lottery_type_id INNER JOIN lottery_fill_user AS fuser ON fopen.lottery_fill_open_id = fuser.lottery_fill_open_id LEFT JOIN lottery_clazz AS clazz ON clazz.lottery_clazz_id = type.lottery_clazz_id
fuser.lottery_fill_status = '1' AND fopen.open_number IS NULL AND type.lottery_type_id = ${lotteryTypeId}
ORDER BY fopen.lottery_fill_creater_date ASC
LIMIT ${firstIndex},${pageSize}
