mapper.select:
SELECT lottery_clazz.lottery_clazz_id, lottery_clazz.lottery_clazz_name AS cName, lottery_clazz.lottery_clazz_parent_id AS cpId, lottery_clazz.lottery_clazz_remark, lottery_clazz.lottery_clazz_level FROM lottery_clazz
OR cName = #{lotteryClazzName,jdbcType=VARCHAR}
OR cpId = #{lotteryClazzParentId,jdbcType=INTEGER}
